mapper.select:
SELECT A.AL_ID, A.LT_ID, A.EMP_ID, A.CREATE_DATE, A.BEG_DATE, A.END_DATE, A.REMARK, A.BILL, A.STATE,
A.EXAMINER, A.EXAMINER_ADV, A.EXAMINER_DATE, A.AUDITOR, A.AUDITOR_ADV, A.AUDITOR_DATE, A.APPROVER,
A.APPROVER_ADV, A.APPROVER_DATE,E.EMP_NAME,EM.EMP_NAME EXAMINER_NAME,EMP.EMP_NAME AUDITOR_NAME,EMPL.EMP_NAME
APPROVER_NAME,LT.LT_NAME,D.DEPT_ID,D.DEPT_NAME,D.DEPT_LEVEL,E.POS_ID,P.POS_NAME,P.LEVAL POS_LEVAL
FROM ASK_LEAVE A
LEFT JOIN EMPLOYEE E
ON A.EMP_ID = E.EMP_ID
LEFT JOIN DEPARTMENT D
ON D.DEPT_ID = E.DEPT_ID
LEFT JOIN POSITION P
ON E.POS_ID = P.POS_ID
LEFT JOIN EMPLOYEE EM
ON EM.EMP_ID = A.EXAMINER
LEFT JOIN EMPLOYEE EMP
ON EMP.EMP_ID = A.AUDITOR
LEFT JOIN EMPLOYEE EMPL
ON EMPL.EMP_ID = A.APPROVER
LEFT JOIN LEAVE_TYPE LT
ON LT.LT_ID = A.LT_ID
WHERE 1 = 1

    AND A.AL_ID = #{alId}


    AND (
    A.EMP_ID = #{empId}
    OR A.EXAMINER = #{empId}
    OR A.AUDITOR = #{empId}
    OR A.APPROVER = #{empId}
    )


    AND E.EMP_NAME LIKE CONCAT('%',#{empName},'%')


    AND E.EMP_ID = #{deptId}


    AND A.STATE = #{state}


    AND A.CREATE_DATE >= #{createBegDate}


    AND A.CREATE_DATE < #{createEndDate}


    AND A.BEG_DATE >= #{qBegDate}


    AND A.END_DATE < #{qEndDate}

ORDER BY A.AL_ID

SELECT A.AL_ID, A.LT_ID, LT.LT_NAME, A.EMP_ID, E.EMP_NAME, A.CREATE_DATE, '1' AS BILL FROM ASK_LEAVE A LEFT JOIN EMPLOYEE E ON A.EMP_ID = E.EMP_ID LEFT JOIN LEAVE_TYPE LT ON A.LT_ID = LT.LT_ID WHERE A.EXAMINER_ADV = 0 AND (A.STATE = 0 OR A.STATE = 2) AND A.EXAMINER = #{empId}
AND A.AL_ID != #{alId}
UNION
SELECT A.AL_ID, A.LT_ID, LT.LT_NAME, A.EMP_ID, E.EMP_NAME, A.CREATE_DATE, '1' AS BILL FROM ASK_LEAVE A LEFT JOIN EMPLOYEE E ON A.EMP_ID = E.EMP_ID LEFT JOIN LEAVE_TYPE LT ON A.LT_ID = LT.LT_ID WHERE A.EXAMINER_ADV = 1 AND A.AUDITOR = #{empId} AND A.AUDITOR_ADV = 0 AND (A.STATE = 0 OR A.STATE = 2)
AND A.AL_ID != #{alId}
UNION
SELECT A.AL_ID, A.LT_ID, LT.LT_NAME, A.EMP_ID, E.EMP_NAME, A.CREATE_DATE, '1' AS BILL FROM ASK_LEAVE A LEFT JOIN EMPLOYEE E ON A.EMP_ID = E.EMP_ID LEFT JOIN LEAVE_TYPE LT ON A.LT_ID = LT.LT_ID WHERE A.EXAMINER_ADV = 1 AND A.AUDITOR_ADV = 1 AND A.APPROVER = #{empId} AND A.APPROVER_ADV = 0 AND (A.STATE = 0 OR A.STATE = 2)
AND A.AL_ID != #{alId}
UNION
SELECT RD.RD_ID AS AL_ID,RD.REA_ID AS LT_ID,RR.REA_NAME AS LT_NAME, RD.EMP_ID,E.EMP_NAME,RD.CREATE_DATE,'2' AS BILL FROM REST_DOWN RD LEFT JOIN EMPLOYEE E ON RD.EMP_ID = E.EMP_ID LEFT JOIN REST_REASON RR ON RR.REA_ID = RD.REA_ID WHERE RD.EXAMINER_ADV = 0 AND (RD.STATE = 0 OR RD.STATE = 2) AND RD.EXAMINER = #{empId}
AND RD.RD_ID != #{rdId}
UNION
SELECT RD.RD_ID AS AL_ID,RD.REA_ID AS LT_ID,RR.REA_NAME AS LT_NAME, RD.EMP_ID,E.EMP_NAME,RD.CREATE_DATE,'2' AS BILL FROM REST_DOWN RD LEFT JOIN EMPLOYEE E ON RD.EMP_ID = E.EMP_ID LEFT JOIN REST_REASON RR ON RR.REA_ID = RD.REA_ID WHERE RD.EXAMINER_ADV = 1 AND RD.AUDITOR = #{empId} AND RD.AUDITOR_ADV = 0 AND (RD.STATE = 0 OR RD.STATE = 2)
AND RD.RD_ID != #{rdId}
UNION
SELECT RD.RD_ID AS AL_ID,RD.REA_ID AS LT_ID,RR.REA_NAME AS LT_NAME, RD.EMP_ID,E.EMP_NAME,RD.CREATE_DATE,'2' AS BILL FROM REST_DOWN RD LEFT JOIN EMPLOYEE E ON RD.EMP_ID = E.EMP_ID LEFT JOIN REST_REASON RR ON RR.REA_ID = RD.REA_ID WHERE RD.EXAMINER_ADV = 1 AND RD.AUDITOR_ADV = 1 AND RD.APPROVER = #{empId} AND RD.APPROVER_ADV = 0 AND (RD.STATE = 0 OR RD.STATE = 2)
AND RD.RD_ID != #{rdId}
UNION
SELECT FC.FC_ID AS AL_ID,FC.FCR_ID AS LT_ID,FCR.FCR_NAME AS LT_NAME,FC.EMP_ID,E.EMP_NAME,FC.CREATE_DATE,'3' AS BILL FROM FORGET_CARD FC LEFT JOIN EMPLOYEE E ON FC.EMP_ID = E.EMP_ID LEFT JOIN FORGET_CARD_REASON FCR ON FCR.FCR_ID = FC.FCR_ID WHERE FC.EXAMINER_ADV = 0 AND (FC.STATE = 0 OR FC.STATE = 2) AND FC.EXAMINER = #{empId}
