SELECT FC.FC_ID AS AL_ID,FC.FCR_ID AS LT_ID,FCR.FCR_NAME AS LT_NAME,FC.EMP_ID,E.EMP_NAME,FC.CREATE_DATE,'3' AS BILL FROM FORGET_CARD FC LEFT JOIN EMPLOYEE E ON FC.EMP_ID = E.EMP_ID LEFT JOIN FORGET_CARD_REASON FCR ON FCR.FCR_ID = FC.FCR_ID WHERE FC.EXAMINER_ADV = 1 AND FC.AUDITOR = #{empId} AND FC.AUDITOR_ADV = 0 AND (FC.STATE = 0 OR FC.STATE = 2)
AND FC.FC_ID != #{fcId}
UNION
SELECT FC.FC_ID AS AL_ID,FC.FCR_ID AS LT_ID,FCR.FCR_NAME AS LT_NAME,FC.EMP_ID,E.EMP_NAME,FC.CREATE_DATE,'3' AS BILL FROM FORGET_CARD FC LEFT JOIN EMPLOYEE E ON FC.EMP_ID = E.EMP_ID LEFT JOIN FORGET_CARD_REASON FCR ON FCR.FCR_ID = FC.FCR_ID WHERE FC.EXAMINER_ADV = 1 AND FC.AUDITOR_ADV = 1 AND FC.APPROVER = #{empId} AND FC.APPROVER_ADV = 0 AND (FC.STATE = 0 OR FC.STATE = 2)
AND FC.FC_ID != #{fcId}
UNION
SELECT RA_ID AS AL_ID,0 AS LT_ID,'' AS LT_NAME,RA.EMP_ID,E.EMP_NAME,RA.CREATE_DATE,'4' AS BILL FROM REIMBURSEMENT_ACCOUNT RA LEFT JOIN EMPLOYEE E ON RA.EMP_ID = E.EMP_ID WHERE RA.EXAMINER_ADV = 0 AND (RA.STATE = 0 OR RA.STATE = 2) AND RA.EXAMINER = #{empId}
AND RA.RA_ID = #{raId}
