SELECT RA_ID AS AL_ID,0 AS LT_ID,'' AS LT_NAME,RA.EMP_ID,E.EMP_NAME,RA.CREATE_DATE,'4' AS BILL FROM REIMBURSEMENT_ACCOUNT RA LEFT JOIN EMPLOYEE E ON RA.EMP_ID = E.EMP_ID WHERE RA.EXAMINER_ADV = 1 AND RA.AUDITOR_ADV = 1 AND RA.APPROVER = #{empId} AND RA.APPROVER_ADV = 0 AND (RA.STATE = 0 OR RA.STATE = 2)
AND RA.RA_ID = #{raId}
