SELECT INFS.INF_ID,0 AS LT_ID,'' AS LT_NAME,INFS.SENDER_ID EMP_ID,E.EMP_NAME,INFS.SEND_DATE CREATE_DATE,'5' AS BILL FROM INFORMATION_SHEET INFS LEFT JOIN EMPLOYEE E ON INFS.SENDER_ID = E.EMP_ID WHERE INFS.SEND_AGREE = 0 AND (INFS.STATE = 0 OR INFS.STATE = 2) AND INFS.EMP_SEND_ID = #{empId}
