mapper.select:
SELECT RA.RA_ID, RA.EMP_ID, RA.CREATE_DATE, RA.RA_REASON, RA.MONEY, RA.STATE, RA.EXAMINER, RA.EXAMINER_ADV, RA.EXAMINER_DATE, RA.AUDITOR, RA.AUDITOR_ADV, RA.AUDITOR_DATE, RA.APPROVER, RA.APPROVER_ADV, RA.APPROVER_DATE, RA.EXAMINER_REMARK, RA.AUDITOR_REMARK, RA.APPROVER_REMARK, E.EMP_NAME, D.DEPT_ID, D.DEPT_NAME, P.POS_ID, P.POS_NAME, EM.EMP_NAME EXAMINER_NAME, EMP.EMP_NAME AUDITOR_NAME, EMPL.EMP_NAME APPROVER_NAME FROM REIMBURSEMENT_ACCOUNT RA LEFT JOIN EMPLOYEE E ON E.EMP_ID = RA.EMP_ID LEFT JOIN DEPARTMENT D ON D.DEPT_ID = E.DEPT_ID LEFT JOIN POSITION P ON E.POS_ID = P.POS_ID LEFT JOIN EMPLOYEE EM ON EM.EMP_ID = RA.EXAMINER LEFT JOIN EMPLOYEE EMP ON EMP.EMP_ID = RA.AUDITOR LEFT JOIN EMPLOYEE EMPL ON EMPL.EMP_ID = RA.APPROVER WHERE 1 = 1
AND RA.RA_ID = #{raId}
AND E.EMP_NAME LIKE CONCAT('%',#{empName},'%')
AND E.EMP_ID = #{deptId}
AND RA.STATE = #{state}
AND RA.CREATE_DATE >= #{createBegDate}
AND RA.CREATE_DATE < #{createEndDate}
AND RA.STATE != 1 AND RA.APPROVER_ADV != 1 AND RA.EXAMINER_ADV != 2 AND RA.AUDITOR_ADV != 2 AND RA.APPROVER_ADV != 2 AND ( RA.EXAMINER_ADV = 0 OR RA.EXAMINER_ADV = 1 OR RA.AUDITOR_ADV = 0 OR RA.AUDITOR_ADV = 1 )
AND RA.STATE != 1 AND RA.APPROVER_ADV = 1
AND RA.STATE != 1 AND ( RA.EXAMINER_ADV = 2 OR RA.AUDITOR_ADV = 2 OR RA.APPROVER_ADV = 2 )
AND RA.STATE = 1
ORDER BY RA.RA_ID
